    delete msg.payload.save; //Needed this to put lineflag in as member

    let output = {},
        len = Object.keys(msg.payload).length,
        keys = Object.keys(msg.payload);

    keys.sort((a, b) => {// sort the keys out to be in order
        //Each element looks like "Par##"
        const lastA = parseInt(a.slice(3)); // Extract the last two digits of 'a' and convert them to a number
        const lastB = parseInt(b.slice(3)); // Extract the last two digits of 'b' and convert them to a number

        return lastA - lastB; // Compare the last two digits in ascending order
    });

    for (let i = 0; i < len; i++) {
        if ((msg.payload[keys[i]] != null) && (msg.payload[keys[i]].name != "") && (msg.payload[keys[i]].value != "") && (msg.payload[keys[i]] != "")) {
            output[keys[i]] = msg.payload[keys[i]];
        }
    }
    msg.payload = output;
}

