    if (msg.payload[key].value < 0) { // Empty values are forced to equal -1
        msg.payload[key].upperVal = -1;
        msg.payload[key].lowerVal = -1;
    } else {
        if (msg.payload[key].upperTol < 0) {
            msg.payload[key].upperVal = -1;
        } else {
            msg.payload[key].upperVal = msg.payload[key].value + msg.payload[key].value * msg.payload[key].upperTol / 100.0;
        }
        if (msg.payload[key].lowerTol < 0) {
            msg.payload[key].lowerVal = -1;
        } else {
            msg.payload[key].lowerVal = msg.payload[key].value - msg.payload[key].value * msg.payload[key].lowerTol / 100.0;
        }
    }
}
return msg;

// Set
if(msg.lineFlag == "clear")
    return null;
let basicFormElements = { // Has the correct order of things
    "GB": {
        "value": "",
        "upperTol": "",
        "lowerTol": ""
    },
    "ACW": {
        "value": "",
        "upperTol": "",
        "lowerTol": ""
    },
    "IR": {
        "value": "",
        "upperTol": "",
        "lowerTol": ""
    },
    "EL": {
        "value": "",
        "upperTol": "",
        "lowerTol": ""
    }
},
    basicFormElementsNames = {
        "GB" : "Ground Bond Value(s)",
        "ACW": "ACW Value(s)",
        "IR": "Insulation Resistance Value(s)",
        "EL": "Earth Leakage Value(s)"
    },
    output = {},
    temp = {},
    missing = [];

if (msg.payload == undefined) { // checks if nothing was in the buffer's context variable, if so, make it empty
    msg.payload = { // copy basicFormsElements into payload
        ...basicFormElements
    };
    for(var key1 in msg.payload) { // Goes through and creates the empty basic data
        for (var key2 in msg.payload[key1]) {
            temp[key2] = -1;
        }
        output[key1] = temp;
        missing.push(basicFormElementsNames[key1]);
    }
} else {
    for (var key1 in basicFormElements) { // Goes through and creates the empty basic data
        if (key1 in msg.payload && msg.payload[key1] != "") { // Check if key exists in current payload
            for (var key2 in basicFormElements[key1]) { // Loop through members of each basic test
                if (key2 in msg.payload[key1]) { // Check if each member in basic test exist
                    temp[key2] = (msg.payload[key1][key2] != null) ? msg.payload[key1][key2] : -1; 
                } else { //Looking at members in basic test
                    temp[key2] = -1;
                    if (!(missing.includes(basicFormElementsNames[key1]))) { // checks to see if missing arr does not have text error msg
                        missing.push(basicFormElementsNames[key1]);
                    }
                }
            }
            output[key1] = (temp)
            temp = {}
        } else { // Looking at basic tests
            for (key2 in basicFormElements[key1]) {
                temp[key2] = -1;
            }
            output[key1] = temp;
            temp = {};
            missing.push(basicFormElementsNames[key1])
        }
    }
}

msg.data  = {
    output: output, //Used in second node downstream to assign payload
    missing: missing
}

if (missing.length > 0) { // If the full basic form is not filled in
    let status = 'The following elements have been left out in the "Basics" section: || ';
    for(let i = 0; i < missing.length; i++) {
        status += missing[i] + " || ";
    }
    msg.payload = status;
    return [ , msg];
} else { // if full basic form is completed
    msg.lineFlag = "completedWithChecks";
    msg.payload = output;
    delete msg.data; // Not needed once checks are completed.
    return [msg, ];
}

