    steps[i] = steps[i].split(',');

    for (let j = 0; j < steps[i].length; j++) {
        if ((j >= 0) && (j <= 2)) {
            steps[i][j] = parseInt(steps[i][j]);
        } else if ((j == 5) || (j == 6)) {
            steps[i][j] = parseFloat(steps[i][j]);
        } else if (j == 10) {
            steps[i][j] =  Boolean(parseInt(steps[i][j]));
        }
    }

    if (counter++ < 0) {
        node.send({
            payload: {
                i: i,
                productCounter: productCounter,
                index: productIndexer.indexOf(productCounter),
                //arr: arr,
                //paulHMIcsv: paulHMIcsv,
                //'steps[i][1]': parseFloat(steps[i][1]),
                //productCounter: productCounter
            }
        });
