    if (steps[i][1] != productCounter) { // Find the instance where product number changes
        let index = productIndexer.indexOf(productCounter);
        if (index != -1)
            paulHMIcsv.push([products[index], arr]);
        arr = [];
        productCounter = steps[i][1]; // Set prodcut number to next product number
    }

    arr.push(steps[i]);

}

