if (flow.get("fileCodeCheck") && flow.get("filenameCheck")) {
    send = [msg, { payload: "Recipe is being saved" }]
} else {
    let text = "";
    //(flow.get("basicFormCheck")) ? null : text += "|| Basic form is not filled in ";
    (flow.get("fileCodeCheck")) ? null : text += "|| File Code is not filled in ";
