node.send(msg);
    //del done

for (let i = 0; i < arr.length; i++) { //Loops through each entry that needs to me recorded
    let len = arr[i].length,
        buf = Buffer.from(arr[i]),
        repeat = Math.floor(len / 2.0),
        remainder = len % 2,
        value = [];
    //Incomplete: This is what you need to copy to the float section
    for (let i = 0; i < repeat && i < entryRegisterLength; i++) { // Loops through the byte pairs of the modbus registers, ignoring the last pair if its an odd number
        value.push(buf.subarray(i * 2, i * 2 + 2).readInt16BE())
    }
    if (remainder) { // If the last pair is odd, set the second byte to 0
        value.push(Buffer.from([buf[buf.length - 1], 0]).readInt16BE());
    }
    for (let i = repeat + remainder; i < entryRegisterLength; i++) {
        value.push(0);
    }

    adresses = entryRegisterLength; //Should always = value.length
    msg.slave_ip = flow.get("slaveIP");

    msg.payload = { "value": value, 'fc': fc, 'unitid': 1, 'address': sa, 'quantity': adresses };
    sa += entryRegisterLength;

    msg.check = {
        value: value,
        valueLength: value.length,//value.length,
        adresses: adresses
    };
    node.send(msg);
}

return null;

